using iskustvohr.Model;
using System;
using System.Text;
using System.Threading;
using System.Threading.Tasks;
using BC = BCrypt.Net.BCrypt;
using iskustvohr.Service.Common;
using iskustvohr.Repository.Common;
using Azure.Communication.Email;
using System.Configuration;

namespace iskustvohr.Service
{
    public class UserService : IUserService
    {
        private readonly string _azureCommunicationConnectionString = ConfigurationManager.ConnectionStrings["azureEmailConnectionString"].ConnectionString;
        protected IUserRepository UserRepository { get; set; }
        protected IRoleRepository RoleRepository { get; set; }

        public UserService(IUserRepository userRepository, IRoleRepository roleRepository)
        {
            UserRepository = userRepository;
            RoleRepository = roleRepository;
        }

        public async Task<int> ConfirmEmailAsync(User user)
        {
            return await UserRepository.ConfirmEmailAsync(user);
        }
        public async Task<int> UpdateUserProfileAsync(User user)
        {
            user.UpdatedAt = DateTime.UtcNow;
            return await UserRepository.UpdateUserProfileAsync(user);
        }

        public async Task<User> GetUserByIdAsync(User user)
        {
            User userToReturn = await UserRepository.GetUserByIdAsync(user);
            return userToReturn;
        }

        public async Task<User> RegisterUserAsync(User user)
        {
            if(await UserRepository.GetUserByEmailAsync(user) != null)
            {
                return null;
            }
            await AssignProperties(user);
            try
            {
                await new EmailClient(_azureCommunicationConnectionString).SendAsync(Azure.WaitUntil.Started,
                    new EmailMessage("DoNotReply@iskustvo.hr",
                    user.Email,
