        public async Task<User> LoginUserAsync(User user)
        {
            User userToReturn = await UserRepository.GetUserByEmailAsync(user);
            userToReturn.Role = await RoleRepository.GetRoleByIdAsync((Role)userToReturn.Role);
            return userToReturn;
        }

        public async Task<int> RequestResetPasswordAsync(User user)
        {
            User userToCheck = await UserRepository.GetUserByEmailAsync(user);
            if(userToCheck == null)
            {
                return 0;
            }
            userToCheck.PasswordResetToken = Convert.ToBase64String(Encoding.UTF8.GetBytes(BC.GenerateSalt(30)));
            userToCheck.PasswordResetTokenExpires = DateTime.UtcNow.AddHours(1);
