        public async Task<int> ChangePasswordAsync(User user)
        {
            User userToCheck = await UserRepository.GetUserByIdAsync(user);
            if (userToCheck == null)
            {
                return 0;
            }
            if (userToCheck.PasswordResetTokenExpires < DateTime.UtcNow || userToCheck.PasswordResetToken == null)
            {
                return -1;
            }
            userToCheck.Password = BC.EnhancedHashPassword(user.Password, 10);
            userToCheck.PasswordResetToken = null;
            return await UserRepository.UpdateUserPasswordAsync(userToCheck);
        }

        private async Task AssignProperties(User user)
        {
            Guid userGuid = Guid.NewGuid();
            user.Id = userGuid;
            user.Email = user.Email.ToLower();
            user.UpdatedAt = DateTime.UtcNow;
            user.CreatedAt = DateTime.UtcNow;
            user.Password = BC.EnhancedHashPassword(user.Password, 10);
            user.IsActive = true;
            user.EmailVerificationId = Guid.NewGuid();

            
        }
    }
